import { Board_Context } from "C:/Users/alexi/Downloads/VsCode Projects/Wubo (Health Website)/Health-Website/my-app/src/App";
import { useContext } from "react";
const Card_Menu = () => {
  const Board = useContext(Board_Context);
  const remove_card_from_list = async (body) => {
    try {
      const result = await fetch(
        "http://localhost:5000/Health-Website/remove_card_from_list",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(body),
        }
      );
      // eslint-disable-next-line no-unused-vars
      const data = result.json();
    } catch (err) {
      console.log(err);
    }
  };

  const delete_card = () => {
    const card_info = {
      card_id: {
        _id: Board.single_board_info[0]._id,
        "board_lists.cards.unique_id": Board.single_board_info[0].board_lists[Board.single_board_info[0].list_index].cards.unique_id,
      },
      cards: {
        $pull: { board_lists: { cards: { unique_id: Board.single_board_info[0].board_lists[Board.single_board_info[0].list_index].cards.unique_id } } },
      },
    };
    remove_card_from_list(card_info);
    Board.set_single_board_info([{
      ...Board.single_board_info[0],
      board_lists: Board.single_board_info[0].board_lists.map((list, index) => {
        if (index === Board.single_board_info[0].list_index) {
          return {
            ...list,
            cards: list.cards.filter((_, index) => index !== Board.single_board_info[0].card_index),
          };
        } else {
          return list;
        }
      }),
    }]);
  }
  
  return (
  <div id="card-menu" onClick={delete_card}>Delete card</div>
  );
};

export default Card_Menu;
