import { useNavigate } from "react-router-dom";
import {useState} from "react";

const Menu_Boards = ({
  board_id,
  board_name,
  update_all_choosen_state,
  update_choosen_state,
  load_board_data,
}) => {
  const [hover_state, set_hover_state] = useState(false);
  const [delete_button_vis, set_delete_button_vis] = useState("none");

  const navigate = useNavigate();
  const onClick = async () => {
    navigate(`/${board_name}`);
    const update_info = {
      id: { _id: board_id },
      choosen: { $set: { choosen: true } },
    };
    const update_all_info = {
      id: {},
      choosen: { $set: { choosen: false } },
    };
    await update_all_choosen_state(update_all_info);
    await update_choosen_state(update_info);
    await load_board_data();
  };

  const toggleHover = () => {
    set_hover_state(hover_state ? false : true);
    set_delete_button_vis(hover_state ? "none" : "flex");
  }
  return (
    <div id="side-menu-boards-container">
    <div id="board-1" onClick={onClick} onMouseEnter={toggleHover} onMouseLeave={toggleHover}>
      {board_name}
    </div>
    <button id="delete-board-button" style={{display: delete_button_vis}}>T</button>
    </div>
  );
};

export default Menu_Boards;
