/* eslint-disable react/jsx-pascal-case */
import { useState, forwardRef } from "react";
import Card_Menu from "./Card_Menu";

const Card = forwardRef(({ name, draggableProps, dragHandleProps }, ref) => {
  const [hover_state, set_hover_state] = useState(false);
  const [edit_button_vis, set_edit_button_vis] = useState("none");

  const toggleHover = () => {
    set_hover_state(!hover_state);
    set_edit_button_vis(!edit_button_vis);
  }

  return (
    <div id="card-container">
      <ul id="card-menu-container">
        <Card_Menu />
      </ul>
      <li id="card" ref={ref} {...draggableProps} {...dragHandleProps} onMouseEnter={toggleHover} >
        {name}
        <button id="edit-card" style={{display: edit_button_vis}}>=</button>
      </li>
    </div>
  );
});

export default Card;
